DROP TABLE IF EXISTS `aitestrebort_script_execution`;
CREATE TABLE `aitestrebort_script_execution`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `create_time` datetime(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) COMMENT '创建时间',
  `update_time` datetime(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) ON UPDATE CURRENT_TIMESTAMP(6) COMMENT '最后修改时间',
  `create_user` int NULL DEFAULT 1 COMMENT '创建人',
  `update_user` int NULL DEFAULT 1 COMMENT '最后修改人',
  `executor_id` int NOT NULL COMMENT '执行人ID',
  `status` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL DEFAULT 'pending' COMMENT '执行状态',
  `environment` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '执行环境',
  `parameters` json NOT NULL COMMENT '执行参数',
  `started_at` datetime(6) NULL DEFAULT NULL COMMENT '开始时间',
  `completed_at` datetime(6) NULL DEFAULT NULL COMMENT '完成时间',
  `execution_time` double NULL DEFAULT NULL COMMENT '执行耗时(秒)',
  `result_data` json NOT NULL COMMENT '执行结果数据',
  `error_message` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '错误信息',
  `stack_trace` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '堆栈跟踪',
  `execution_log` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '执行日志',
  `celery_task_id` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '任务ID',
  `script_id` int NOT NULL COMMENT '自动化脚本',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `fk_wharttes_wharttes_f3549ee6`(`script_id` ASC) USING BTREE,
  CONSTRAINT `fk_wharttes_wharttes_f3549ee6` FOREIGN KEY (`script_id`) REFERENCES `aitestrebort_automation_script` (`id`) ON DELETE CASCADE ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci COMMENT = '脚本执行记录表' ROW_FORMAT = Dynamic;

