INSERT INTO `auto_test_python_script` VALUES (1, '2025-12-20 22:39:38.395462', '2025-12-20 22:39:38.395468', 1, 1, 'base_template', '# -*- coding: utf-8 -*-\n\n\"\"\"\n请按如下规范进行文件及函数命名\n    数据库操作：\n        函数文件名：服务名称_sql\n            如：当前服务为测试平台：test_platform_sql\n\n        函数名：服务名称_函数作用\n            如：获取测试平台用户名：def test_platform_get_user_name()\n\n    非数据库操作：\n        函数文件名：当前辅助函数类型\n            如：数据处理类：format_data\n\n        函数名：函数文件名称_函数作用\n            如：字典转json：def format_data_dict_to_json()\n\n注：\n    1、建议在本地pycharm中将脚本编写完毕，并且调试通过后再粘到测试平台\n    2、当编写脚本需要使用第三方库时，需向管理员确认是否已安装此第三方库，以及版本是否正确\n    3、当有多个函数文件中，有相同名字的函数时，正好某次运行测试时两个函数文件都加载了，对于同名函数，则会以最后一次加载的函数为准\n    4、每次运行测试时，会传入一个名为 env 的变量，变量值为当前运行的环境，有需要时，可以根据环境判断执行对应的逻辑\n\"\"\"\n', '自定义函数文件使用规范说明', 0, 'test');
INSERT INTO `auto_test_python_script` VALUES (3, '2025-12-20 22:39:38.396722', '2025-12-21 14:04:03.857119', 1, 1, 'database_template', '# -*- coding: utf-8 -*-\n\nimport sys\nfrom contextlib import contextmanager\n\nimport pymysql\n\nthis = sys.modules[__name__]\nif hasattr(this, \"env\") is False:\n    setattr(this, \"env\", \"test\")\n\nif env == \'test\':\n    testDbMysql = {\n        \"host\": \"\",\n        \"user\": \"\",\n        \"password\": \"\",\n        \"port\": \"\",\n        \"database\": \"\",\n        \"charset\": \"utf8\"\n    }\nelif env == \'dev\':\n    pass\nelif env == \'uat\':\n    pass\n\n\nclass TestMysql:\n    \"\"\"初始化数据库连接\"\"\"\n\n    @contextmanager\n    def connect_db(self):\n        \"\"\" 每次执行前连接 \"\"\"\n        conn = pymysql.connect(**testDbMysql)\n        cursor = conn.cursor(cursor=pymysql.cursors.DictCursor)\n        try:\n            yield cursor\n            conn.commit()\n        except:  # 提交异常时需要回滚事件\n            conn.rollback()\n        finally:  # 关闭连接\n            cursor.close()\n            conn.close()\n\n    def execute(self, sql):\n        \"\"\"\n        @param sql: 执行的sql模板（非查询类sql使用）\n        \"\"\"\n        with self.connect_db() as db:\n            db.execute(sql)\n\n    def fetchone(self, mysql):\n        \"\"\" 查询一条数据 \"\"\"\n        data = None\n        with self.connect_db() as db:\n            db.execute(mysql)\n            data = db.fetchone()\n        return data\n\n    def fetchall(self, mysql):\n        \"\"\" 查询所有数据 \"\"\"\n        data = None\n        with self.connect_db() as db:\n            db.execute(mysql)\n            data = db.fetchall()\n        return data\n\n\ndatabase_template = TestMysql()  # 实例化对象，避免操作多个数据库时冲突，实例变量名为脚本名字\n\n\ndef database_template_insert_or_update_user(mobile, password, token, user_id=\'\', company_name=\'\', company_id=\'\',\n                                            role=None):\n    \"\"\" 插入或更新token \"\"\"\n    if database_template_get_info_by_mobile(mobile):  # 如果有就更新\n        database_template_update_token(mobile, token, user_id, company_name, company_id)\n    else:  # 没有就插入\n        database_template_insert_user(mobile, password, token, role, company_id, company_name, user_id)\n\n\ndef database_template_get_info_by_mobile(mobile):\n    \"\"\" 根据手机号获取用户数据 \"\"\"\n    msql = f\"SELECT * FROM auto_test_user WHERE mobile = \'{mobile}\' and env=\'{env}\';\"\n    print(msql)\n    return database_template.fetchone(msql)\n\n\ndef database_template_update_token(mobile, token, user_id, company_name, company_id):\n    \"\"\" 更新用户token \"\"\"\n    m_sql = f\"UPDATE auto_test_user set u_token=\'{token}\', user_id=\'{user_id}\', company_name=\'{company_name}\', company_id=\'{company_id}\' WHERE mobile=\'{mobile}\' and env=\'{env}\';\"\n    print(m_sql)\n    database_template.execute(m_sql)\n\n\ndef database_template_insert_user(mobile, password, token, role=None, company_id=None, company_name=None, user_id=None):\n    \"\"\" 插入用户信息 \"\"\"\n    m_sql = f\"INSERT INTO `auto_test_user` (`create_time`, `update_time`, `create_user`, `update_user`, `mobile`, `password`, `u_token`, `role`, `company_id`, `company_name`, `user_id`, `comment`, `env`) VALUES (NULL, NULL, NULL, NULL, \'{mobile}\', \'{password}\', \'{token}\', \'{role}\', \'{company_id}\', \'{company_name}\', \'{user_id}\', NULL, \'{env}\');\"\n    print(m_sql)\n    database_template.execute(m_sql)\n\n\nif __name__ == \'__main__\':\n    print(database_template_get_info_by_mobile(\'15756623400\'))\n', '数据库操作类型的自定义函数文件模板', 1, 'test');
INSERT INTO `auto_test_python_script` VALUES (4, '2025-12-21 14:17:26.089352', '2025-12-21 14:17:26.089363', 1, 1, 'base_template', '# -*- coding: utf-8 -*-\n\n\"\"\"\n请按如下规范进行文件及函数命名\n    数据库操作：\n        函数文件名：服务名称_sql\n            如：当前服务为测试平台：test_platform_sql\n\n        函数名：服务名称_函数作用\n            如：获取测试平台用户名：def test_platform_get_user_name()\n\n    非数据库操作：\n        函数文件名：当前辅助函数类型\n            如：数据处理类：format_data\n\n        函数名：函数文件名称_函数作用\n            如：字典转json：def format_data_dict_to_json()\n\n注：\n    1、建议在本地pycharm中将脚本编写完毕，并且调试通过后再粘到测试平台\n    2、当编写脚本需要使用第三方库时，需向管理员确认是否已安装此第三方库，以及版本是否正确\n    3、当有多个函数文件中，有相同名字的函数时，正好某次运行测试时两个函数文件都加载了，对于同名函数，则会以最后一次加载的函数为准\n    4、每次运行测试时，会传入一个名为 env 的变量，变量值为当前运行的环境，有需要时，可以根据环境判断执行对应的逻辑\n\"\"\"\n', '自定义函数文件使用规范说明', NULL, 'test');

